
public class ShortHands {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=5;
		//System.out.println(i++);
		System.out.println(i);

		i*=2;
		System.out.println(i);
		i=5;
		i+=2;
		System.out.println(i);
		i=5;
		i-=2;
		System.out.println(i);
		
		byte b=8;
		b*=2.5;
		System.out.println((double)b);
		
	}

}
